import { useEffect } from "react";
import { useState } from "react";
import { notesService } from "../../services/notesService";
import { useForm } from "react-hook-form";

const CreateNote = () => {
    const [question, setQuestion] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [emotion, setEmotion] = useState(null);

    const user = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null;
    const { register, formState: { errors }, handleSubmit } = useForm({ mode: 'onSubmit', reValidateMode: 'onChange' });

    const emotions = { 'happy': 1, 'ok': 2, 'sad': 3 };

    useEffect(() => {
        setIsLoading(true);

        notesService.getOne(user)
            .then(response => {
                setQuestion(response.question);
                setIsLoading(false);
            })
    }, [user.user_id]);

    const sendAnswer = (data) => {
        notesService.createOne(user, { avatar: emotion, text: data.description })
            .then(response => {
                console.log(response);
            })
            .catch(err => {
                console.log(err);
            });
    } 

    return (
        <section className="flex flex-col items-center justify-center">
            {isLoading && <p>Loading ...</p>}
            {!isLoading && !emotion &&
                <article className="flex flex-col items-center mt-20">
                    <h1 className="text-8xl my-10 text-center">{question}</h1>
                    {Object.entries(emotions).map((kvp, i) => <p key={i} onClick={() => setEmotion(kvp[1])}>{kvp[0]}</p>)}
                </article>
            }

            {!isLoading && emotion &&
                <article className="flex flex-col items-center">
                    <h1 className="text-8xl my-10 text-center">Why do you feel this way?</h1>

                    <form onSubmit={handleSubmit(sendAnswer)} className="flex flex-col">
                        <input {...register('description', { required: { value: true } })} name="description" type="text" className="form-input" />
                        <input type="submit" value="Send Answer" />
                    </form>
                </article>
            }
        </section>
    );
}

export default CreateNote;
