import { useState, useEffect } from "react";
import {notesService} from '../../../services/notesService';
import { formatDate } from "../../../utils";
import { Link } from "react-router-dom";

import './AllNotes.css';

import { useNavigate } from "react-router";

const AllNotes = () => {
    const [notes, setNotes] = useState([]);
    const user = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null;
    const navigate = useNavigate();

    useEffect(() => {
        notesService.getReport(user.user_id)
            .then((response) => {
                console.log(response);
                setNotes(response.map(obj => { 
                    return { id: obj.id, description: obj.description, date: formatDate(obj.date)};
                }));
            })
            .catch(err => {
                navigate('/login');
            });
    }, [user.user_id]);


    return (
        <section className="flex flex-wrap pt-5">
            {notes.map((note, i) => 
                <article className="text-black bg-white rounded-xl w-80 my-6 mx-6" key={i}>
                    <p>{note.description}</p>

                    <div className="dateMade">
                        <p>Date: {note.date}</p>
                    </div>

                    <Link to={`/notes/${user.user_id}/${note.id}`}>Read note</Link>
                </article>
            )}
        </section>
    );
}

export default AllNotes;
