import React from 'react';
import { Bar, BarChart, CartesianAxis, XAxis, YAxis, Tooltip, ResponsiveContainer, CartesianGrid, Legend } from 'recharts';
import { formatDate } from '../../utils';

const ReportChart = ({ emotions }) => {
  let data = [];

  emotions.forEach(emotionObj => {
    data.push({name: formatDate(emotionObj.date), uv: Number(emotionObj.emotion) })
  });


  return (
    <>
      <ResponsiveContainer width={1200} height={550}>
        <BarChart
          width={500}
          height={300}
          data={data}
        >
          <CartesianGrid strokeDasharray={"3 3"} />
          <XAxis dataKey="name" />
          <YAxis domain={0, 5} />
          <Tooltip />
          <Legend />
          <Bar type="monotone" dataKey="uv" stroke="blue" />
        </BarChart>
      </ResponsiveContainer>
    </>
  );
}

export default ReportChart;
