import { useEffect } from "react";
import { useState } from "react";
import { notesService } from "../../services/notesService";
import { useForm } from "react-hook-form";

const CreateNote = () => {
    const [question, setQuestion] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [emotion, setEmotion] = useState(null);
    const [description, setDescription] = useState(null);

    const user = localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null;
    const { register, formState: { errors }, handleSubmit } = useForm({ mode: 'onSubmit', reValidateMode: 'onChange' });


    useEffect(() => {
        setIsLoading(true);

        notesService.getOne(user)
            .then(response => {
                setQuestion(response.question);
                setIsLoading(false);
            })
    }, [user.user_id]);

    const selectEmotion = (data) => setEmotion(data.emotion);


    return (
        <section className="flex flex-col items-center justify-center">
            {isLoading && <p>Loading ...</p>}
            {!isLoading && !emotion &&
                <article>
                    <h1 className="text-8xl mt-10">{question}</h1>

                    <input onChange={e => setEmotion(e.target.value)}  name="emotion" type="number" className="form-input" />
                </article>
            }
        </section>
    );
}

export default CreateNote;
